var currTrack = API.getData('currtrack')
var images = API.getData('img')

var index = currTrack.minSpectra.indexClosest;

API.createData('image', images[index]);

var currentSpectra = API.getData('currentSpectra')
var data = API.getData('data')

var currTrack = API.getData('currtrack')
var index = currTrack.minSpectra.indexClosest

var result = data.reduce(function (r, a) {
        r[a.group] = r[a.group] || [];
        r[a.group].push(a);
        return r;
    }, Object.create(null));

var indik = []
Object.keys(result).forEach(e => 
    indik.push({"index": e,
    "indicator": {
      "type": "sparkline",
      "value": result[e].map(x => x.dataMatrix[index*2+1]),
      "_options": {
        "type": "box",
        "raw": false,
        "minValue": -50,
        "maxValue": 20,
        "boxLineColor": "black",
        "boxFillColor": "lightgrey",
        "whiskerColor": "black",
        "medianColor": "red",
        "lineColor": "black",
        "target": currentSpectra[index*2+1],
        "targetColor": "green"
      }
    }})
)

indik.push({"index": "All",
    "indicator": {
      "type": "sparkline",
      "value": data.map(x => x.dataMatrix[index*2+1]),
      "_options": {
