let data = API.getData('data')

let x = data[0].dataMatrix.filter(function(value, index, Arr) {
    return index % 2 == 0
})

let y = data.map( x => 
    x.dataMatrix.filter((value, index, Arr) => 
        index % 2 == 1
    )
)

